package array_display;

public class array_arranging {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]={2,3,7,9,28,79};
		for(int i=0;i<a.length;i++){  
			if(a[i]%2==0){  
			System.out.println(a[i]);  
			}  
			}  
		for(int i=0;i<a.length;i++){  
		if(a[i]%2!=0){  
		System.out.println(a[i]);  
		}  
		}  
		
	}

}
	